function disableOthers(b){var c=b.name;var a=[];var f=b.parentNode.getElementsByTagName("button");for(var e=0;e<f.length;e++){var d=f[e];if(d.name!=c){a.push(d)}}for(var e=0;e<a.length;e++){var d=a[e];document.getElementById(d.name).style.display="none";d.style.borderStyle="outset"}}function toggle(a){var c=a.name;var d=document.getElementById(c).style.display;var b="";if(d=="block"){d="none";b="outset"}else{d="block";b="inset"}document.getElementById(c).style.display=d;a.style.borderStyle=b}function toggleOne(a){var c=a.name;var d=document.getElementById(c).style.display;var b="";if(d=="block"){d="none";b="outset"}else{d="block";b="inset"}document.getElementById(c).style.display=d;a.style.borderStyle=b;disableOthers(a)}function select_option(a,b){for(var c=0;c<a.options.length;c++){if(c==b){a.options[c].selected=true}else{a.options[c].selected=false}}}function jumpTo(b){var e=b.value;var a=new String(window.location);var d=a.search("#");if(d!=-1){var a=a.slice(0,d)}var c=a+"#"+e;select_option(b,0);window.location=c}function disableHighLight(a){var c=a.parentNode.getElementsByTagName("a");for(var e=0;e<c.length;e++){var d=c[e].className.split(" ");var f=[];for(var b=0;b<d.length;b++){if(d[b]!="highLight"){f.push(d[b])}}c[e].className=f.join("  ")}}function replaceContent(b,c,a){var d=new XMLHttpRequest();d.onreadystatechange=function(){if(d.readyState==4){if(d.status==200){document.getElementById(a).innerHTML=d.responseText;disableHighLight(b);b.className=b.className+" highLight"}else{document.getElementById(a).innerHTML="Error code "+d.status}}};d.open("GET",c,true);d.send(null)}function loadRecord(a,c,b){var d=a.value;$(""+c+"").load(""+b+d+"")};